mapper.update:
update comment
set uid = #{uid,jdbcType=INTEGER},
  pid = #{pid,jdbcType=INTEGER},
  rate = #{rate,jdbcType=DOUBLE},
  content = #{content,jdbcType=VARCHAR}
where cid = #{cid,jdbcType=INTEGER}

mapper.select:
select cid, uid, pid, rate, content
from comment
where cid = #{cid,jdbcType=INTEGER}
