mapper.insert:
insert into image (iid, pid, image
  )
values (#{iid,jdbcType=INTEGER}, #{pid,jdbcType=INTEGER}, #{image,jdbcType=LONGVARBINARY}
  )

mapper.update:
update image
set pid = #{pid,jdbcType=INTEGER},
  image = #{image,jdbcType=LONGVARBINARY}
where iid = #{iid,jdbcType=INTEGER}

mapper.select:
select iid, pid, image
from image
where iid = #{iid,jdbcType=INTEGER}
